package com.evergent.corejava.generalprogramming;

public class Pattern4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int count=0;
		for(int i=5; i>=1 ;i--) {
			for(int j=5;j>=1;j--) {
				count+=1;
				System.out.print(count);
				System.out.print(" ");
				
			}
			System.out.println();
		}

	}

}
